import React from 'react';
import { getFirestore } from '../../firebase';

const db = firebase.getFirestore()
const Driver = () => {
  const [rideRequest, setRideRequest] = useState({
    destination: '',
    pickupLocation: ''
  });

  function handleRideRequest(e) {
    e.preventDefault();
    db.collection('requests').add({
      destination: rideRequest.destination,
      pickupLocation: rideRequest.pickupLocation,
      status: 'pending',
      passenger: '<passenger-name>'
    });
  }

  db.collection('requests')
    .where('passenger', '==', '<passenger-name>')
    .onSnapshot((snapshot) => {
      snapshot.docChanges().forEach((change) => {
        if (change.type === 'modified') {
          console.log('Ride request updated: ', change.doc.data());
        }
      });
    });
    return (
      <div>
      <form onSubmit={handleRideRequest}>
        <input
          type="text"
          placeholder="Destination"
          value={rideRequest.destination}
          onChange={e => setRideRequest({ ...rideRequest, destination: e.target.value })}
        />
        <input
          type="text"
          placeholder="Pickup Location"
          value={rideRequest.pickupLocation}
          onChange={e => setRideRequest({ ...rideRequest, pickupLocation: e.target.value })}
        />
        <button type="submit">Solicitar Viagem</button>
      </form>
    </div>
  );
  };

  export default Driver;
