      <div>

        {users.map((user) => {
          return (
            <div>
              <h1>
                Name: name={user.name}
              </h1>
              <h1>
                Name: age={user.age}
              </h1>
            </div>
          );
        })}

      </div>
    );
  };

  export default Passenger;
