import './task.css'
import {useState} from 'react'
import TaskItem from './TaskItem'
import EditTask from './EditTask'
import { doc, updateDoc, deleteDoc} from "firebase/firestore";
import {db} from '../../firebase'

function Task({id, title, description, completed, created}) {

  const [checked, setChecked] = useState(completed)
  const [open, setOpen] = useState({edit:false, view:false})

  const handleClose = () => {
    setOpen({edit:false, view:false})
  }

  /* function to update firestore */
  const handleChange = async () => {
    const taskDocRef = doc(db, 'tasks', id)
    try{
      await updateDoc(taskDocRef, {
        completed: checked
      })
    } catch (err) {
      alert(err)
    }
  }

  /* function to delete a document from firstore */ 
  const handleDelete = async () => {
    const taskDocRef = doc(db, 'tasks', id)
    try{
      await deleteDoc(taskDocRef)
    } catch (err) {
      alert(err)
    }
  }

  return (
    <div className={`task ${checked && 'task--borderColor'}`}>

      <div className='task__body'>
        <h2>{title}</h2>
        <p>{description}</p>



        <div className='task__buttons'>
          <div className='task__deleteNedit'>
            {/* <button 
              className='task__editButton' 
              onClick={() => setOpen({...open, edit : true})}>
              Edit
            </button> But√£o de Editar Task*/}
            <button className='task__deleteButton' onClick={handleDelete}>Recusar</button>
            <input 
          id={`checkbox-${id}`} 
          className='checkbox-custom'
          name="checkbox" 
          checked={checked}
          onChange={handleChange}
          type="checkbox" />
        <label 
          htmlFor={`checkbox-${id}`} 
          className="checkbox-custom-label" 
          onClick={() => setChecked(!checked)} >Aceitar</label>
          </div>
          {/* <button 
            onClick={() => setOpen({...open, view: true})}>
            View
          </button> */}
        </div>
      </div>

      {open.view &&
        <TaskItem 

          title={title} 
          description={description}
          date={created} 
          />
          // Em caso de erro adicionar o codigo open={open.view} 
          // Em caso de erro adicionar o codigo open={onClose={handleClose}} 
      }

      {/* {open.edit &&
        <EditTask 
          onClose={handleClose} 
          toEditTitle={title} 
          toEditDescription={description} 
          open={open.edit}
          id={id} />
      } */}

    </div>
  )
}

export default Task
