import {collection, addDoc, Timestamp} from 'firebase/firestore'
import React, {useEffect, useReducer, useState} from 'react';
import {db} from '../../firebase'


function ItemList() {
  const [items, setItems] = useState([]);

  const handleAddItem = () => {
    // Crie uma referência para a coleção do Firebase que você irá armazenar seus itens
    const itemsRef = firebase.firestore().collection('items');

    // Crie um novo item com os dados que você deseja armazenar
    const newItem = {
      name: 'Novo item',
      description: 'Descrição do novo item',
      date: new Date()
    };

    // Adicione o novo item ao Firebase e atualize o estado da lista
    itemsRef.add(newItem)
      .then((docRef) => {
        // Adicione o ID gerado pelo Firebase para o novo item
        const updatedItem = { ...newItem, id: docRef.id };

        // Atualize o estado da lista com o novo item
        setItems([...items, updatedItem]);
      })
      .catch((error) => {
        console.error('Erro ao adicionar item:', error);
      });
  };

  return (
    <div>
      <button onClick={handleAddItem}>Adicionar item</button>
      <ul>
        {items.map((item) => (
          <li key={item.id}>{item.name} - {item.description}</li>
        ))}
      </ul>
    </div>
  );
}

export default ItemList;
